mapper.insert:
insert into mghboard values(
mgh_seq_idx.nextval, #{name}, #{password}, #{subject}, #{content}, sysdate , 0 ,#{ip}, #{addr1}, #{addr2}
)

mapper.select:
select R.* from (
	select rownum rnum, Q.* from (
		select * from mghboard order by idx desc
	) Q
	where rownum <= #{endNo}
) R
where rnum >= #{startNo}
