mapper.insert:
insert into mguboard values(
mga_seq_idx.nextval, #{ref}, #{name} ,#{subject}, #{content}, sysdate , 0 ,#{ip}, #{addr1}, #{addr2} , 0
)

mapper.select:
select R.* from (
	select rownum rnum, Q.* from (
		select * from mguboard order by idx desc
	) Q
	where rownum <= #{endNo}
) R
where rnum >= #{startNo}
